INSERT INTO `restaurant` (`id`, `name`, `phone_number`, `description`, `website`, `logo`, `dine_in`, `hours`, `enabled`, `address_id`, `chain_id`, `pitmaster_id`) VALUES (4, 'JABO\'S BAR-BE-Q', '3037994432', 'Jerome \"Jabo\" Lawson, founder of Jabo\'s Bar-Be-Q, \nstarted his business from a small rock house in Mcloud Oklahoma in 1950. \nFolks came from all over to get the great taste of Jabo\'s Bar-Be-Q. \nJabo\'s son, Dwight, now known as \"Jabo\", continues this legend in Greenwood Village, which he started back in 1992. \nJabo has expanded the recipe to include several tantalizing flavors of sauce.  All of which are made from scratch.  \nWe at Jabo\'s hope you enjoy all of these delicious flavors that expand well over 22 different flavors.', 'http://www.jabosbarbeq.com/index.html', 'data:image/jpeg;base64,', 1, 'Tuesday to Saturday\n11:00 am to 8:00 pm\nClosed Sunday and Monday', 1, 4, NULL, 4);
