START TRANSACTION;
USE `bbqdb`;
INSERT INTO `sauce` (`id`, `name`, `description`, `image`) VALUES (1, 'Texas Style Mop', 'this sauce as more of a thin \"glaze\" that moistens the meat and adds flavor as it smokes. ', 'data:image/jpeg;base64,');
INSERT INTO `sauce` (`id`, `name`, `description`, `image`) VALUES (2, 'Kansas City-Style sauce', 'Kansas City BBQ sauce is the perfect balance of sweet and tangy. It\'s rich from the molasses, bright from the acidic tomatoes and vinegar, and has just the right amount of heat from the chili powder. It\'s a classic, ketchup-based BBQ sauce that is a must-have for chicken, ribs, and more', 'data:image/jpeg;base64,');
INSERT INTO `sauce` (`id`, `name`, `description`, `image`) VALUES (3, 'Alabama White Sauce', 'This incredibly easy-to-make Alabama white barbecue sauce was invented in 1925 by Robert \'Big Bob\' Gibson at Big Bob Gibson\'s Bar-B-Q restaurant, in Decatur. Don\'t let this mixture\'s appearance stop you from trying what is a devastatingly delicious marinade, basting sauce, and serving sauce', 'data:image/jpeg;base64,');
INSERT INTO `sauce` (`id`, `name`, `description`, `image`) VALUES (4, 'South Carolina-Style Mustard Sauce', 'Known as \"Carolina Gold\", this is a tangy mustard BBQ sauce. This is perfect mixed with pulled pork and then served with fries or mash', 'data:image/jpeg;base64,');

COMMIT;


-- -----------------------------------------------------
-- Data for table `style`
-- -----------------------------------------------------
START TRANSACTION;
USE `bbqdb`;
INSERT INTO `style` (`id`, `name`, `description`) VALUES (1, 'Texas Style', 'Texas Style bbq is descriped as, Our pits are 100% wood fired with oak, a slower burning wood than the mesquite used by others.');
INSERT INTO `style` (`id`, `name`, `description`) VALUES (2, 'Kansas City Style', 'Kansas City Style of slowly smoked meats that originated from the pits of Henry Perry in the early 20th century in Kansas City, Missouri');
INSERT INTO `style` (`id`, `name`, `description`) VALUES (3, 'Memphis Style ', 'Memphis Style BBq is slow cooked in a pit and ribs can be prepared either \"dry\" or \"wet\". \"Dry\" ribs are covered with a dry rub consisting of salt and various spices before cooking and are normally eaten without sauce. \"Wet\" ribs are brushed with sauce before, during, and after cooking.');
INSERT INTO `style` (`id`, `name`, `description`) VALUES (4, 'Georgia Style', 'Georgia barbecue restaurants serve chicken and ribs, chopped pork is by far the most popular meat. Pitmasters tend to cook shoulders or hams over oak and hickory, and Georgia-style sauce is a red tomato-based concoction that varies greatly in sweetness, heat, and thickness from one spot to another');

COMMIT;


